        if (url.includes('bit.ly') || url.includes('tinyurl')) phishingScore += 30;
        if (phishingDomains.some(domain => url.includes(domain))) phishingScore += 40;

        // Add some randomness for demo purposes
        phishingScore += Math.random() * 20;
        phishingScore = Math.min(100, phishingScore);

        const isPhishing = phishingScore > 60;
        const confidence = Math.min(95, Math.max(65, phishingScore + Math.random() * 15));

        return {
            prediction: isPhishing ? 'Phishing' : 'Safe',
            confidence: Math.round(confidence),
            features,
            analysis_method: 'Heuristic URL Analysis',
            risk_factors: isPhishing ? [
                'Suspicious domain',
                'Unusual URL structure',
                'High character count'
            ] : []
        };
    },

    trainEnhancedModel: async () => {
        await simulateDelay(3000);

        return {
            status: 'success',
            message: 'Model training simulation completed',
            accuracy: 0.92 + Math.random() * 0.05,
            training_time: '45 seconds'
        };
    }
};

