import React, { useState } from 'react';
import {
    Card,
    CardContent,
    Typography,
    TextField,
    Button,
    Box,
    Alert,
    CircularProgress,
    Chip,
    Divider
} from '@mui/material';
import { urlAPI } from '../api';

const URLChecker = () => {
    const [url, setUrl] = useState('');
    const [result, setResult] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        if (!url.trim()) {
            setError('Please enter a URL');
            return;
        }

        setLoading(true);
        setError(null);
        setResult(null);

        try {
            const response = await urlAPI.predictURL({ url: url.trim() });
            setResult(response);
        } catch (err) {
            setError(err.response?.data?.detail || 'Failed to analyze URL');
        } finally {
            setLoading(false);
        }
    };

    const getResultColor = (prediction) => {
        return prediction === 'Phishing' ? 'error' : 'success';
    };

    const getConfidenceColor = (confidence) => {
        if (confidence >= 80) return 'error';
        if (confidence >= 60) return 'warning';
        return 'success';
    };

    return (
        <Card sx={{ maxWidth: 800, mx: 'auto', mt: 3 }}>
            <CardContent>
                <Typography variant="h5" gutterBottom>
                    üîó URL Phishing Checker
                </Typography>
                <Typography variant="body2" color="text.secondary" sx={{ mb: 3 }}>
                    Check if a URL is potentially malicious using our enhanced AI model
                </Typography>

                <Box component="form" onSubmit={handleSubmit} sx={{ mb: 3 }}>
                    <TextField
                        fullWidth
                        label="Enter URL to check"
                        value={url}
                        onChange={(e) => setUrl(e.target.value)}
                        placeholder="https://example.com"
                        sx={{ mb: 2 }}
                        helperText="Enter a complete URL including http:// or https://"
                    />
                    <Button
                        type="submit"
                        variant="contained"
                        disabled={loading}
                        sx={{ minWidth: 120 }}
                    >
                        {loading ? <CircularProgress size={20} /> : 'Check URL'}
                    </Button>
                </Box>

                {error && (
                    <Alert severity="error" sx={{ mb: 2 }}>
                        {error}
                    </Alert>
                )}

                {result && (
                    <Box>
                        <Alert 
                            severity={getResultColor(result.prediction)} 
                            sx={{ mb: 2 }}
                        >
                            <Typography variant="h6">
                                {result.prediction === 'Phishing' ? '‚ö†Ô∏è Phishing URL Detected' : '‚úÖ URL Appears Safe'}
                            </Typography>
                            <Typography variant="body2">
                                Confidence: {result.confidence}%
                            </Typography>
                        </Alert>

                        <Card variant="outlined" sx={{ mb: 2 }}>
                            <CardContent>
                                <Typography variant="h6" gutterBottom>
                                    Analysis Details
                                </Typography>
                                <Box sx={{ display: 'flex', gap: 1, mb: 2 }}>
                                    <Chip 
                                        label={`${result.prediction}`}
                                        color={getResultColor(result.prediction)}
                                        size="small"
                                    />
                                    <Chip 
                                        label={`${result.confidence}% confidence`}
                                        color={getConfidenceColor(result.confidence)}
                                        size="small"
                                    />
                                    <Chip 
                                        label={result.model_type}
                                        variant="outlined"
                                        size="small"
                                    />
                                </Box>
                            </CardContent>
                        </Card>

                        {result.features && (
                            <Card variant="outlined">
                                <CardContent>
                                    <Typography variant="h6" gutterBottom>
                                        URL Features Analysis
                                    </Typography>
                                    <Box sx={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: 1 }}>
                                        {Object.entries(result.features).map(([key, value]) => (
                                            <Box key={key} sx={{ display: 'flex', justifyContent: 'space-between', p: 1 }}>
                                                <Typography variant="body2" color="text.secondary">
                                                    {key.replace(/_/g, ' ')}:
                                                </Typography>
                                                <Typography variant="body2" fontWeight="bold">
                                                    {typeof value === 'number' ? value.toFixed(2) : value.toString()}
                                                </Typography>
                                            </Box>
                                        ))}
                                    </Box>
                                </CardContent>
                            </Card>
                        )}
                    </Box>
                )}
            </CardContent>
        </Card>
    );
};

export default URLChecker;
